    public function run()
    {
        Model::unguard();

        $photo = Photo::first();

        if (!$photo) {
            $photo = Photo::create([
                'name' => 'Default',
                'description' => 'Default'
            ]);
        }

        $array = $this->getData();

        foreach ($array as $value) {
            $item = json_decode(json_encode($value), true);
            $item['manipulations'] = [];
            $item['custom_properties'] = [];
            $item['responsive_images'] = [];
            $item['generated_conversions'] = ["preview" => true];
            $photo->media()->create($item);
        }
    }

